mapper.select:
SELECT u.username username, u.email email, u.nickname nickname, u.mobile mobile, u.create_date createDate, IFNULL(s.sns_type,'') snsType FROM users u LEFT JOIN sns_info s ON u.user_id=s.user_id
username LIKE CONCAT('%',#{searchWord},'%')
ORDER BY create_date DESC LIMIT #{beginRow}, #{rowPerPage}

mapper.insert:
INSERT INTO
manager( manager_id, manager_password, manager_name, manager_nickname, manager_address, manager_phone_number, manager_level, create_date
) VALUES ( #{managerId}, #{managerPassword}, #{managerName}, #{managerNickname}, #{managerAddress}, #{managerPhoneNumber}, DEFAULT(manager_level), NOW()
)
