mapper.select:
SELECT pr.apply_product_sales_delivery_id applyId, img.img_name imgName, pd.product_name productName, FORMAT(pr.product_price, N'#,0') productPrice, cate.category_sub_name categorySubName FROM used_product_registration pr LEFT JOIN apply_product_sales_delivery pd ON pr.apply_product_sales_delivery_id = pd.apply_product_sales_delivery_id LEFT JOIN apply_product_sales_delivery_img img ON pd.apply_product_sales_delivery_id = img.apply_product_sales_delivery_id LEFT JOIN category_sub cate ON pd.category_sub_id = cate.category_sub_id
AND pd.productName LIKE CONCAT ('%',#{searchWord},'%')
GROUP BY productName ORDER BY pr.create_date DESC LIMIT #{beginRow}, #{rowPerPage}

SELECT img_name imgName
FROM apply_product_sales_delivery_img
WHERE apply_product_sales_delivery_id = #{applyProductSalesDeliveryId}

mapper.update:
UPDATE product_confirmation_registration
SET registration_state = 1
WHERE apply_product_sales_delivery_id = #{applyProductSalesDeliveryId}
